import logo from './logo.svg';
import './App.css';
import React, { useState } from 'react';

var current_guess = 1;
var current_col = 0;
var current_guess_word = "";
var correct_word = "QUEEN";


function App() {

  document.addEventListener('DOMContentLoaded', function() {
    var all_keys = document.getElementsByClassName('key');
 //  console.log(all_keys);
    for (let i = 0; i < all_keys.length; i++) {
      let curr_key = all_keys[i];
      curr_key.addEventListener('click', () => type_letter(curr_key))
    }

    function type_letter(letter) {
      var letter_value = letter.innerHTML;
      var grid_items = document.getElementsByClassName("guess_1");

      if (letter_value == "ENTER") {
        submit_guess(grid_items);
      }
      else if (letter_value == "BACK") {
        delete_letter_from_guess(grid_items);
      }
      else {
        var curr_space = grid_items[current_col];
        curr_space.innerHTML = letter_value;
        current_guess_word = current_guess_word + letter_value + ""
        console.log(current_guess_word);
        current_col = current_col + 1;
      }




     // var g = document.getElementsByClassName('guess_1');
      //console.log(g)


    }

    function submit_guess(grid_items) {
      current_col = 0;
      current_guess = current_guess + 1;

      for (let i = 0; i < 5; i++) {
        var guess_tile = grid_items[i];
        var tile_letter = guess_tile.innerHTML;

        if (tile_letter == correct_word[i]) {
          guess_tile.style.backgroundColor = 'green';
        }
        else if (correct_word.includes(tile_letter)) {
          guess_tile.style.backgroundColor = '#ffae42';
        }
      }


    }

    function delete_letter_from_guess(grid_items) {
      current_guess_word = current_guess_word.substring(0, current_guess_word.length - 1);
      current_col = current_col - 1;
      let current_tile = grid_items[current_col];
      current_tile.innerHTML = " "

    }



  });




  return (
    <div className="App">
      <h1>W O R D L E</h1>
    <div className = "flex-grid"> 
      <div className="grid-container">


    <div className="grid-item guess_1">1</div>
    <div className="grid-item guess_1">1</div>
    <div className="grid-item guess_1">1</div>
    <div className="grid-item guess_1">1</div>
    <div className="grid-item guess_1">1</div>



    <div className="grid-item guess_2">6</div>
    <div className="grid-item guess_2">7</div>
    <div className="grid-item guess_2">8</div>
    <div className="grid-item guess_2">9</div>
    <div className="grid-item guess_2">10</div>



    <div className="grid-item guess_3">11</div>
    <div className="grid-item guess_3">12</div>
    <div className="grid-item guess_3">13</div>
    <div className="grid-item guess_3">14</div>
    <div className="grid-item guess_3">15</div>


    <div className="grid-item guess_4">16</div>
    <div className="grid-item guess_4">17</div>
    <div className="grid-item guess_4">18</div>
    <div className="grid-item guess_4">19</div>
    <div className="grid-item guess_4">20</div>



    <div className="grid-item guess_5">21</div>
    <div className="grid-item guess_5">22</div>
    <div className="grid-item guess_5">23</div>
    <div className="grid-item guess_5">24</div>
    <div className="grid-item guess_5">25</div>

  </div>
  </div>

  <div className="flex-keyboard">

  <div className="keyboard">
    <div className="row1">
    <div className="key">Q</div>
    <div className="key">W</div>
    <div className="key">E</div>
    <div className="key">R</div>
    <div className="key">T</div>
    <div className="key">Y</div>
    <div className="key">U</div>
    <div className="key">I</div>
    <div className="key">O</div>
    <div className="key">P</div>
    </div>

    <div className="row2">
    <div className="key">A</div>
    <div className="key">S</div>
    <div className="key">D</div>
    <div className="key">F</div>
    <div className="key">G</div>
    <div className="key">H</div>
    <div className="key">J</div>
    <div className="key">K</div>
    <div className="key">L</div>
    </div>



    <div className="row3">
    <div className="key">ENTER</div>
    <div className="key">Z</div>
    <div className="key">X</div>
    <div className="key">C</div>
    <div className="key">V</div>
    <div className="key">B</div>
    <div className="key">N</div>
    <div className="key">M</div>
    <div className="key">BACK</div>

    </div>
    </div>




  </div>


    </div>
  );
}

export default App;
